def test_convert_html_to_blocks_basic(sample_html_content_persian):
    document_title = "شاستارکا"
    blocks = convert_html_to_blocks(
        html=sample_html_content_persian,
        document_title=document_title,
        should_translate=False,
        global_translation_map={},
        frequent_words_to_exclude=set(),
        inverse_redirection_map={},
    )

    assert len(blocks) == 1
    assert blocks[0].document_title == document_title


def test_problematic_list(sample_html_content_with_problematic_list):
    document_title = "Torneo de las Cuatro Naciones 1895"
    blocks = convert_html_to_blocks(
        html=sample_html_content_with_problematic_list,
        document_title=document_title,
        should_translate=False,
        global_translation_map={},
        frequent_words_to_exclude=set(),
        inverse_redirection_map={},
    )

    # This HTML has issue and therefore the main table in the article won't be extracted. However, the rest of the content should be extracted.
    assert len(blocks) == 2
    assert blocks[0].document_title == document_title


def test_multiple_header_levels(sample_html_with_multiple_header_levels):
    document_title = "هاروکی موراکامی"
    blocks = convert_html_to_blocks(
        html=sample_html_with_multiple_header_levels,
        document_title=document_title,
        should_translate=False,
        global_translation_map={},
        frequent_words_to_exclude=set(),
        inverse_redirection_map={},
    )

    assert len(blocks) == 8
    assert blocks[-2].section_title == "آثار > دیگر کتاب\u200cها"


